mapper.select:
select
from rmt_lostinfo where happen_dt >= #{since}
AND kind_cd = #{petType}
order by happen_dt desc

mapper.update:
update rmt_lostinfo
set
process_state = #{processState}
where
	desertion_no = #{desertionNo}

mapper.insert:
insert into pets (
	pet_name,
	pet_type,
	kind_cd
) values (
	#{petName},
	#{petType},
	#{kindCd}
);
